interface GargsStatic {
	date_type_config:	string;
	date_type:	number;
	opt_date_type:	number;
	start_time:	string;
	end_time:	string;
	is_succ:	number;
	phone:	string;
	type:	number;
	page_num:	number;
	page_count:	number;
}
declare module "g_args" {
    export = g_args;
}
declare var g_args: GargsStatic;
declare var g_account: string;
declare var g_account_role: any;
declare var g_adminid: any;
interface RowData {
	recordid	:any;
	phone	:any;
	message	:any;
	send_time	:any;
	type	:any;
	user_ip	:any;
	receive_content	:any;
	is_success	:any;
}

